from tkinter.ttk import Style
from tkinter import PhotoImage


class StylesInterface(Style):

    ServiceGroupStyleImage = PhotoImage
    ServiceGroupStyleImageFocus = PhotoImage
    ButtonStyleImage = PhotoImage

    def __init__(self, master=None):
        super().__init__(master=master)

        self.ServiceGroupStyleImage = PhotoImage("frameBorder", data="""""")
        self.ServiceGroupStyleImageFocus = PhotoImage("frameFocusBorder", data="""""")

        self.element_create("RoundedFrameElement", "image", "frameBorder",
        ("focus", "frameFocusBorder"), border=16, sticky="nsew")

        self.layout("RoundedFrame", [("RoundedFrameElement", {"sticky": "nsew"})])

        self.configure("TFrame", borderwidth=0,bg="#616161")
